const t=JSON.parse('{"key":"v-2f40f3f3","path":"/OS/OS%E6%93%8D%E4%BD%9C%E7%B3%BB%E7%BB%9F%E7%9F%A5%E8%AF%86%E7%82%B9%E6%80%BB%E7%BB%93/25.html","title":"死锁","lang":"zh-CN","frontmatter":{"title":"死锁","icon":"desktop","date":"2023-07-03T00:00:00.000Z","description":"死锁 如果一个进程集合中的每一个进程都在等待只能由该进程集合中的其它进程才能引发的事件，那么该进程集合就是死锁（deadlock）的。 大部分死锁和资源有关。资源是进程/线程为了完成工作，所需要的实体 可重用资源：CPU、内存、文件、设备…… 可消耗资源：缓冲区产品、IPC的消息、中断…… 可抢占资源：CPU、内存（有挂起）…… 不可抢占资源：打印机、内存（无挂起）、文件…… 死锁发生的条件 互斥（Mutual exclusion） 任何时刻只能有一个进程使用一个资源实例","head":[["meta",{"property":"og:url","content":"https://cfddd.github.io/OS/OS%E6%93%8D%E4%BD%9C%E7%B3%BB%E7%BB%9F%E7%9F%A5%E8%AF%86%E7%82%B9%E6%80%BB%E7%BB%93/25.html"}],["meta",{"property":"og:site_name","content":"俄罗斯刺沙蓬"}],["meta",{"property":"og:title","content":"死锁"}],["meta",{"property":"og:description","content":"死锁 如果一个进程集合中的每一个进程都在等待只能由该进程集合中的其它进程才能引发的事件，那么该进程集合就是死锁（deadlock）的。 大部分死锁和资源有关。资源是进程/线程为了完成工作，所需要的实体 可重用资源：CPU、内存、文件、设备…… 可消耗资源：缓冲区产品、IPC的消息、中断…… 可抢占资源：CPU、内存（有挂起）…… 不可抢占资源：打印机、内存（无挂起）、文件…… 死锁发生的条件 互斥（Mutual exclusion） 任何时刻只能有一个进程使用一个资源实例"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-07-05T07:04:01.000Z"}],["meta",{"property":"article:author","content":"俄罗斯刺沙蓬"}],["meta",{"property":"article:published_time","content":"2023-07-03T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2023-07-05T07:04:01.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"死锁\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2023-07-03T00:00:00.000Z\\",\\"dateModified\\":\\"2023-07-05T07:04:01.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"俄罗斯刺沙蓬\\",\\"url\\":\\"https://cfddd.github.io/\\"}]}"]]},"headers":[{"level":2,"title":"死锁发生的条件","slug":"死锁发生的条件","link":"#死锁发生的条件","children":[]},{"level":2,"title":"死锁的预防","slug":"死锁的预防","link":"#死锁的预防","children":[]},{"level":2,"title":"终极方法：鸵鸟方法","slug":"终极方法-鸵鸟方法","link":"#终极方法-鸵鸟方法","children":[]}],"git":{"createdTime":1688540641000,"updatedTime":1688540641000,"contributors":[{"name":"cfd","email":"2909047212@qq.com","commits":1}]},"readingTime":{"minutes":3.33,"words":999},"filePathRelative":"OS/OS操作系统知识点总结/25.md","localizedDate":"2023年7月3日","excerpt":"<h1> 死锁</h1>\\n<p>如果一个进程集合中的每一个进程都在等待只能由该进程集合中的其它进程才能引发的事件，那么该进程集合就是死锁（deadlock）的。</p>\\n<p>大部分死锁和资源有关。资源是进程/线程为了完成工作，所需要的实体</p>\\n<ul>\\n<li>可重用资源：CPU、内存、文件、设备……</li>\\n<li>可消耗资源：缓冲区产品、IPC的消息、中断……</li>\\n<li>可抢占资源：CPU、内存（有挂起）……</li>\\n<li>不可抢占资源：打印机、内存（无挂起）、文件……</li>\\n</ul>\\n<h2> 死锁发生的条件</h2>\\n<p><strong>互斥（Mutual exclusion）</strong><br>\\n任何时刻只能有一个进程使用一个资源实例</p>","autoDesc":true}');export{t as data};
